```js
// isInput 判断的是是否合并结束， 默认结束
let isInput = true;
getOrderDemo
	.on(
		"input",
		throttle(function () {
			// 如果合并结束完成则， 进行下一步的逻辑操作; 没有结束合并  不会执行;
			if (isInput) {
				let val = $(this)
					.val()
					.replace(/\s|\r|\t|\n/g, "");
				// ...code there 执行对应的逻辑操作;
			}
		}, 500)
	)
	.on("keyup", function () {})
	.on("compositionstart", function () {
		// 当执行如图二的情况下，说明还在合并，并没有结束
		isInput = false;
	})
	.on("compositionend", function () {
		// 执行到这一步说明  合并结束， 当合并结束时，执行input
		isInput = true;
	});
```

