}
```

## 

```ts
// 
import { useRegisterSW } from "virtual:pwa-register/vue";
const { offlineReady, needRefresh, updateServiceWorker } = useRegisterSW({
	// 3s
	onRegistered(r) {
		if (r) {
			setInterval(() => {
				r.update();
			}, 1000 * 3);
		}
	},
});
```

## 

```vue
<script setup lang="ts">
import { useRegisterSW } from "virtual:pwa-register/vue";
import { ref, watch } from "vue";
import { ElMessage } from "element-plus";

const { offlineReady, needRefresh, updateServiceWorker } = useRegisterSW({
	onRegistered(r) {
		if (r && !check.value) {
			setInterval(() => {
				r.update();
			}, 1000 * 3);
		}
	},
});

// needRefresh.value 

// 
const check = ref(needRefresh.value);

const loading = ref(false);
const btnText = ref("");
// 
const updataPage = async () => {
	loading.value = true;
	btnText.value = "...";
	await updateServiceWorker();
};

// 
const close = () => {
	check.value = false;
	loading.value = false;
	btnText.value = "";
};
// 
const beforeClose = (done: any) => {
	if (!loading.value) {
		return done();
	}
	ElMessage({
		message: ", ...",
		type: "warning",
	});
};
// 3sneedRefresh.value
watch(
	() => needRefresh.value,
	val => {
		check.value = val;
	}
);
</script>

<template>
	<div>======11111213+++---</div>
	<el-dialog
		v-model="check"
		append-to-body
		class="w-update"
		draggable
		width="410px"
		:before-close="beforeClose"
	>
		<template #header></template>
		<div class="w-update-icon"></div>
		<h3></h3>
		<p></p>

		<p></p>
		<p style="color: #ff0000; font-size: 12px">
			()~
		</p>
		<template #footer>
			<el-button type="primary" @click="updataPage" :loading="loading">
				{{ btnText }}
			</el-button>
			<el-button type="default" :loading="loading" @click="close">

			</el-button>
		</template>
	</el-dialog>
</template>

<style scoped>
.w-update {
	position: relative;
}
.w-update-icon {
	position: absolute;
	top: -50px;
	left: 50%;
	width: 100px;
	height: 100px;
	line-height: 100px;
	text-align: center;
	background: linear-gradient(
		50deg,
		var(--el-color-primary),
		var(--el-color-primary-light-7)
	);
	border-radius: 50%;
	transform: translateX(-50%);
}
.w-update-icon i {
	font-size: 50px;
	color: var(--el-color-white);
}

.w-update-cup {
	position: absolute;
	right: 20px;
	bottom: 70px;
	font-size: 80px;
	-webkit-text-fill-color: transparent;
	background-image: linear-gradient(
		var(--el-color-primary-light-7),
		var(--el-color-primary-light-9)
	);
	background-clip: text;
}
</style>

<style>
.w-update.el-dialog {
	margin-top: 30vh !important;
	border-radius: 15px;
}

.w-update .el-dialog__body {
	margin: 0 40px 0 40px;
}

